package com.learning.springboot.web;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.security.config.annotation.authentication.builders.AuthenticationManagerBuilder;import org.springframework.security.config.annotation.web.builders.HttpSecurity;import org.springframework.security.config.annotation.web.configuration.WebSecurityConfigurerAdapter;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.SessionAttributes;@Controller@SessionAttributes("UserName")public class SecurityConfiguration extends WebSecurityConfigurerAdapter {	@Autowired	public void configureGlobalSecurity(AuthenticationManagerBuilder auth) throws Exception {		auth.inMemoryAuthentication().withUser("gaurav").password("saxena").roles("USER", "ADMIN");	}	@Override	protected void configure(HttpSecurity http) throws Exception {		http.authorizeRequests().antMatchers("/login").permitAll().antMatchers("/", "/*todo*/**")				.access("hasRole('USER')").and().formLogin();	}}